import React, { useEffect ,useState } from 'react'
import {user} from"../Join/Join";
import socketIo, { Socket } from "socket.io-client";
import "./Chat.css";
import sendLogo1 from "../Image/sendLogo.jpg";
import Message from "../Message/Message";
import ReactScrollToBottom from "react-scroll-to-bottom";


let socket;
const ENDPOINT='http://localhost:4500';

const Chat = () => {

  //  const [id,setid]= useState("")
    const [id, setid] = useState("");
    const [messages,setMessages]=useState([])


    const send= () =>{
        const message=document.getElementById('chatInput').value;
        socket.emit('message',{message,id});
        document.getElementById('chatInput').value="";

    }
   
    useEffect(() => {

    socket=socketIo(ENDPOINT,{transports:['websocket']}); 

      socket.on('connect',()=>{
          alert('Connected');
          setid(socket.id);
      })
      console.log(socket);

      socket.emit('joined',{ user })
      
      socket.on('welcome',(data) =>{
         setMessages([messages,data]);
          console.log(data.user,data.message);
      })

      socket.on('userJoined',(data)=>{
       setMessages([messages,data]);
          console.log(data.user,data.message);
      })
      socket.on('leave',(data)=>{
       setMessages([messages,data]);
          console.log(data.user,data.message);
      })
      return () => {
       socket.emit('Disconnect')
        socket.off();
      }
    }, [])
    
  

    useEffect(() => {
      socket.on('sendMessage',(data)=>{
        setMessages([messages,data]);
          console.log(data.user,data.message,data.id);
      })
    
      return () => {
    
      }
      
    }, [])
    

    return (
    <div className="chatPage">
        <div className="chatContainer">
            <div className="header"></div>
            <ReactScrollToBottom className="chatBox">
                { messages.map((item,i) => <Message message={item.messages} />)}
                
                {/* <messages>
                 <Message message={'hey whats up'} classs='left'/>
                <Message message={'hey whats up'} classs='right'/>
                </messages> */}
            </ReactScrollToBottom>
            <div className="inputBox">
                <input type="text" id="chatInput"></input>
                <button onClick={send} className="sendBtn"><img src={sendLogo1} alt="Send"></img></button>
            </div>
        </div>
    </div>
  )

}

export default Chat
